import React, { useEffect, useState } from "react";
import "./App.css";
import Header from "./components/Header.js";
import InputField from "./components/InputField.js";
import BodyPanel from "./components/BodyPanel.js";

function App() {
  //state
  const [requestedIngredients, setRequestedIngredients] = useState([
    "apple",
    "bacon",
  ]);

  const [resultIngredients, setResultIngredients] = useState([]);

  useEffect(() => {
    async function fetchData() {
      //ask the server for a list of common pairings of requestedIngredients
      const response = await fetch("/api", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: requestedIngredients,
      });

      const data = await response.json();
      console.log(data);
    }
    fetchData();
  }, [requestedIngredients]);

  //component tree
  return (
    <>
      <Header />
      <InputField />
      <BodyPanel
        requestedIngredients={requestedIngredients}
        setRequestedIngredients={setRequestedIngredients}
        resultIngredients={resultIngredients}
        setResultIngredients={setResultIngredients}
      />
    </>
  );
}

export default App;
